/// <reference types="vitest" />
import { defineConfig } from 'vitest/config'

export default defineConfig({
  test: {
    environment: 'happy-dom',
    globals: true,
    setupFiles: ['./test/polyfills.ts', './test/setup.ts'],
    coverage: {
      provider: 'v8',
      reporter: ['text', 'json', 'html'],
      exclude: [
        'node_modules/',
        'test/',
        'coverage/',
        '**/*.test.ts',
        '**/*.spec.ts',
        'vitest.config.ts',
      ],
      thresholds: {
        global: {
          branches: 70,
          functions: 70,
          lines: 70,
          statements: 70,
        },
      },
    },
  },
  resolve: {
    alias: {
      '@': '/app/javascript',
    },
  },
})

import { defineConfig } from 'vite'
import RubyPlugin from 'vite-plugin-ruby'

export default defineConfig({
  plugins: [
    RubyPlugin(),
  ],
  resolve: {
    alias: {
      '@': '/app/javascript',
    },
    // Deduplicate Lit modules to prevent multiple versions
    dedupe: ['lit', 'lit-element', 'lit-html', '@lit/reactive-element'],
  },
  optimizeDeps: {
    // Force Vite to pre-bundle and deduplicate Lit modules
    include: ['lit', '@alenaksu/json-viewer'],
    force: true, // Force re-optimization even if deps haven't changed
  },
  esbuild: {
    target: 'es2020',
  },
  build: {
    target: 'es2020',
    rollupOptions: {
      output: {
        manualChunks: {
          vendor: ['@hotwired/stimulus', '@hotwired/turbo-rails'],
          lit: ['lit', '@alenaksu/json-viewer'],
        },
      },
    },
  },
})

import { Application } from '@hotwired/stimulus'
import { beforeEach } from 'vitest'

declare global {
  var Rails: {
    fire: (element: Element, event: string, data?: unknown) => boolean
    ajax: (options: Record<string, unknown>) => void
  }
  var Turbo: {
    visit: (url: string, options?: Record<string, unknown>) => void
    cache: {
      clear: () => void
    }
  }
}

// Global test setup
(global as Record<string, unknown>).Application = Application

// Mock console methods to avoid noise in tests
const originalConsole = global.console
global.console = {
  ...originalConsole,
  warn: vi.fn(),
  error: vi.fn(),
} as Console

// Setup DOM before each test
beforeEach(() => {
  document.body.innerHTML = ''
  document.head.innerHTML = ''
})

// Mock window.location
Object.defineProperty(window, 'location', {
  value: {
    href: 'http://localhost:3000',
    pathname: '/',
    search: '',
    hash: '',
  },
  writable: true,
})

// Mock window.fetch for tests that might use it
// Note: fetch is already available globally in the test environment

// Mock Rails UJS for forms
global.Rails = {
  fire: vi.fn(),
  ajax: vi.fn(),
}

// Mock Turbo for StimulusJS controllers that might use it
global.Turbo = {
  visit: vi.fn(),
  cache: {
    clear: vi.fn(),
  },
}

// Helper to create a Stimulus application for testing
export function createApplication(): Application {
  const application = Application.start()
  application.debug = false
  return application
}

// Helper to register a controller for testing
export function registerController(
  application: Application, 
  name: string, 
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  controller: any
): void {
  application.register(name, controller)
}

// Helper to create DOM elements with Stimulus data attributes
export function createElementWithController(
  controllerName: string, 
  html: string = '<div></div>'
): Element {
  const div = document.createElement('div')
  div.innerHTML = html
  const element = div.firstChild as Element
  element.setAttribute('data-controller', controllerName)
  return element
}
